import React from 'react'
import './servicePage.css'
import { useParams , useNavigate} from 'react-router-dom';

import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { getServicesDetails } from '../../redux/actions/serviceAction'
import { addToServiceCart } from '../../redux/actions/cartActions'


const ServicePage = () => {

  const [qty, setQty] = useState(1);
  const dispatch = useDispatch();
  const { id } = useParams();
  const navigate = useNavigate();

  const serviceDetails = useSelector((state) => state.getServicesDetails);

  const {loading, error, service} = serviceDetails;

  useEffect(()=>{
    if(service && id!== service._id){
      dispatch(getServicesDetails(id));
    }
  },[dispatch,id,service])

  const addToCartHandler = () =>{
        dispatch(addToServiceCart(service._id, qty));
        navigate('/cart');
    } 

  return (
    <div className='service_screen'>
        <div className="service_left">
        <div className="service_left_image">
          <img src= '/logo192.png' alt='Service' />
        </div>
        <div className="service_left_info">
          <p className='service_left_name'>Service</p>
          <p>$499 Only</p>
          <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quas totam deleniti, blanditiis necessitatibus culpa reprehenderit beatae corrupti praesentium sint illo quibusdam dolorem veniam ex id.</p>
        </div>
      </div>
      <div className="service_right">
        <div className='service_right_info'>
          <p>
            Price : <span>$499</span>
          </p>
          <p>
            Status : <span>{'In Stock'}</span>
          </p>
          <p >
            Qty :
            <select >
              <option value="1">1</option>
              <option value="1">2</option>
              <option value="1">3</option>
              <option value="1">4</option>
            </select>
          </p>
          <p>
            <button type='button'>Add To Cart</button>
          </p>
        </div>
      </div>
    </div>
  )
}

export default ServicePage
